---------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------CREATING TABLES-----------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------CREATE TABLE CUSTOMERS(	CUSTOMER_ID INT PRIMARY KEY,	FIRST_NAME VARCHAR2(20) ,	LAST_NAME VARCHAR2(20) ,	    GENDER  VARCHAR2(20) CHECK (GENDER IN ('Male','Female','Other')),			CUSTOMER_EMAIL VARCHAR2(20) ,	    CUSTOMER_PHONE VARCHAR2(20) ,	PREMIUM NUMBER(1) 	);CREATE TABLE EMPLOYEES(    EMPLOYEE_ID INT PRIMARY KEY,    FIRST_NAME VARCHAR2(20) ,    LAST_NAME VARCHAR2(20) ,    GENDER  VARCHAR2(20) CHECK (GENDER IN ('Male','Female','Other')),    EMPLOYEE_EMAIL VARCHAR2(30) ,    EMPLOYEE_PHONE VARCHAR2(20) ,    SALARY NUMBER,    PROVISION INT );CREATE TABLE SUPPLIERS(	SUPPLIER_ID INT PRIMARY KEY NOT NULL,    SUPPLIER_NAME VARCHAR2(20),	SUPPLIER_COUNTRY VARCHAR2(20)	);CREATE TABLE PRODUCTS(	PRODUCT_ID INT PRIMARY KEY NOT NULL,	SUPPLIER_ID INT NOT NULL,    CRYSTAL_NAME VARCHAR2(20),    price_per_gram INT,    colour VARCHAR2(20),        CONSTRAINT FK1 FOREIGN KEY (SUPPLIER_ID) REFERENCES SUPPLIERS(SUPPLIER_ID));CREATE TABLE ORDERS(	ORDER_ID INT PRIMARY KEY NOT NULL,	PRODUCT_ID INT NOT NULL,	CUSTOMER_ID INT NOT NULL,	    EMPLOYEE_ID INT NOT NULL,	ORDER_DATE DATE,    PRODUCT_WEIGHT INT,    PRICE INT,		CONSTRAINT FK2 FOREIGN KEY (PRODUCT_ID) REFERENCES PRODUCTS(PRODUCT_ID),	CONSTRAINT FK3 FOREIGN KEY (CUSTOMER_ID) REFERENCES CUSTOMERS(CUSTOMER_ID),    CONSTRAINT FK4 FOREIGN KEY (EMPLOYEE_ID) REFERENCES EMPLOYEES(EMPLOYEE_ID));-----------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------INSERTING INTO TABLES--------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------INSERT INTO suppliers VALUES (1, 'Rockwell Diamonds', 'Canada');INSERT INTO suppliers VALUES (2, 'Anglo American plc', 'United Kingdom');INSERT INTO suppliers VALUES (3, 'Gem Diamonds', 'United Kingdom');INSERT INTO suppliers VALUES (4, 'Petra Diamonds', 'United States');INSERT INTO suppliers VALUES (5, 'Lucara', 'Canada');INSERT INTO suppliers VALUES (6, 'Dominion', 'Canada');INSERT INTO suppliers VALUES (7, 'Debswana', 'Botswana');INSERT INTO suppliers VALUES (8, 'Rio-Tinto', 'Australia');INSERT INTO suppliers VALUES (9, 'Alrosa', 'Russia');INSERT INTO suppliers VALUES (10, 'De Beers', 'United Kingdom');INSERT INTO products VALUES (1, 1, 'Diamond', 50000, 'White');INSERT INTO products VALUES (2, 2, 'Emerald', 10000, 'Green');INSERT INTO products VALUES (3, 3, 'Opal', 8000, 'Blue');INSERT INTO products VALUES (4, 4, 'Ruby', 20000, 'Red');INSERT INTO products VALUES (5, 5, 'Sapphire', 9000, 'Blue');INSERT INTO products VALUES (6, 6, 'Spinel', 6000, 'Red');INSERT INTO products VALUES (7, 7, 'Topaz', 12000, 'White');INSERT INTO products VALUES (8, 8, 'Amber', 7000, 'Orange');INSERT INTO products VALUES (9, 9, 'Amethyst', 18000, 'Purple');INSERT INTO products VALUES (10, 10, 'Beryl', 14000, 'Blue');INSERT INTO products VALUES (11, 1, 'Calci