        (SELECT MAX(price) FROM orders));-- 7 List non-premium customer(s) who has the most expensive order --(INNER JOIN superior)SELECT * FROM customersJOIN orders ON orders.customer_id = customers.customer_idWHERE customers.premium = 0ORDER BY orders.price DESCFETCH FIRST 1 ROWS ONLY;-- 8 List most frequently bought product (name and total weight) for each genderSELECT C1.gender, P1.crystal_name, SUM(O1.product_weight) AS TOTAL_WEIGHT FROM products P1JOIN orders O1 ON O1.product_id = P1.product_idJOIN customers C1 ON C1.customer_id = O1.customer_idHAVING ( C1.gender, P1.crystal_name, SUM(O1.product_weight) ) = (    SELECT C2.gender, P2.crystal_name, SUM(O2.product_weight) AS TOTAL_WEIGHT FROM products P2    JOIN orders O2 ON O2.product_id = P2.product_id    JOIN customers C2 ON C2.customer_id = O2.customer_id    WHERE C2.gender = C1.gender    GROUP BY C2.gender, P2.crystal_name    ORDER BY TOTAL_WEIGHT DESC    FETCH FIRST 1 ROW ONLY    )GROUP BY C1.gender, P1.crystal_name;-- 9 List total price of orders for each genderSELECT customers.gender, SUM(orders.price) AS TOTAL_PRICE FROM customersJOIN orders ON customers.customer_id = orders.customer_idGROUP BY customers.gender;-- 10 List the most expensive order (product name, price (calculated by Function 1)) for each countrySELECT suppliers.supplier_country, products.crystal_name, orders.price AS BIGGEST_ORDER FROM productsJOIN orders ON orders.product_id = products.product_idJOIN suppliers ON suppliers.supplier_id = products.product_idWHERE (suppliers.supplier_country, orders.price) IN    (    SELECT suppliers.supplier_country, MAX(orders.price) AS BIGGEST_ORDER FROM products    JOIN orders ON orders.product_id = products.product_id    JOIN suppliers ON suppliers.supplier_id = products.product_id    GROUP BY suppliers.supplier_country); -----------------------------------------------------------------------------------------------------------------------------------------